function addBox(title, desc, button, img, link) {
	let box = `<div class="box">
		<img src="${img}" alt="${title}" class="box-img">
		<div class="box-content">
			<div>
				<div class="box-title">${title}</div>
				<div class="box-desc">${desc}</div>
			</div>
			<a href="${link}" class="button-round box-button">${button}</a>
		</div>
	</div>`;

	$(".box-container").append(box);
}

